    @Override
    public boolean validateAllControls() {
        boolean lolos;
        lolos = true;
        double d;

        try {
            d = Integer.parseInt(txtNamaGejala.getSQLValue());
            lolos = false;
        } catch (java.lang.NumberFormatException ex) {
            ex.printStackTrace();
            //bener
        }
        
        try {
            d = Integer.parseInt(txtDeskripsi.getSQLValue());
            lolos = false;
        } catch (java.lang.NumberFormatException ex) {
            ex.printStackTrace();
        }

        if (lolos) {
            if (txtDeskripsi.getSQLValue().equals("") && txtDeskripsi.getSQLValue().length() <= 2) {
                return false;
            }


            if (txtNamaGejala.getSQLValue().equals("") && txtNamaGejala.getSQLValue().length() <= 2) {
                return false;
            }
           
        }else return false;

        return true;


    }

