    @Override
    protected String delegateGetInsertSql() {
        return "insert into gejala(nama_gejala," +
                "deskripsi)" +
                "values('" +
                txtNamaGejala.getText() + "','" +
                txtDeskripsi.getText() + "'" 
                 + ")";
    }

    @Override
    protected String delegateGetUpdateSql(String rowId) {
        return "update gejala set nama_gejala='" + txtNamaGejala.getText() + "'," +
                "deskripsi='" + txtDeskripsi.getText() + "' " +
                " where idgejala=" + rowId;
    }

